import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

const recipeDone = JSON.parse(localStorage.getItem('doneRecipes'));
const recipeInProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
console.log(recipeDone);
console.log(recipeInProgress);

function VerifyStart({ id }) {
  if (Object
    .keys(recipeDone).length === 0 || recipeDone.find((recipe) => id !== recipe.id)) {
    return (
      <Link
        to={ `${window.location.pathname}/in-progress` }
      >
        <button
          type="button"
          data-testid="start-recipe-btn"
          className="start-btn"
