import React from 'react';
import { Link } from 'react-router-dom';

const recipeDone = JSON.parse(localStorage.getItem('doneRecipes'));
const recipeInProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));

function verifyStart(id) {
  if (!recipeDone || recipeDone.find((recipe) => id !== recipe.id)) {
    return (
      <Link
        data-testid="start-recipe-btn"
        to={ `${window.location.pathname}/in-progress` }
      >
        Iniciar Receita
      </Link>
    );
  }

  if (recipeInProgress
    && (recipeInProgress.cocktails[id] === id || recipeInProgress.meals[id] === id)) {
    return (
      <Link
        data-testid="start-recipe-btn"
        to={ `${window.location.pathname}/in-progress` }
      >
        Continuar Receita
      </Link>
    );
  }
}

export default verifyStart;
